// Hide Max List Items - v1.3 - Minified
(function($){$.fn.extend({hideMaxListItems:function(options){var defaults={max:3,speed:1000,moreText:"READ MORE",lessText:"READ LESS",moreHTML:'<p class="maxlist-more"><a href="#"></a></p>',};var options=$.extend(defaults,options);return this.each(function(){var op=options;var totalListItems=$(this).children("li").length;var speedPerLI;if(totalListItems>0){speedPerLI=Math.round(op.speed/totalListItems);if(speedPerLI<1){speedPerLI=1}}else{speedPerLI=0}if((totalListItems>0)&&(totalListItems>op.max)){$(this).children("li").each(function(index){if((index+1)>op.max){$(this).hide(0);$(this).addClass("maxlist-hidden")}});$(this).after(op.moreHTML);$(this).next(".maxlist-more").children("a").text(op.moreText);$(this).next(".maxlist-more").children("a").click(function(e){var listElements=$(this).parent().prev("ul, ol").children("li");listElements=listElements.slice(op.max);if($(this).text()==op.moreText){$(this).text(op.lessText);var i=0;(function(){$(listElements[i++]||[]).slideToggle(speedPerLI,arguments.callee)})()}else{$(this).text(op.moreText);var i=listElements.length-1;(function(){$(listElements[i--]||[]).slideToggle(speedPerLI,arguments.callee)})()}e.preventDefault()})}})}})})(jQuery);
